import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Product } from '../../interfaces/product';

@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html'
})
export class ProductCardComponent {

  /**
   * Indica si se está cargando el producto
   */
  @Input()
  public isLoading: boolean = false;

  /**
   * Producto a mostrar
   */
  @Input()
  public product?: Product;

  /**
   * Evento que se emite cuando se hace click en el botón de ver detalles
   */
  @Output()
  public onClickProduct = new EventEmitter<Product>();

  ngOnInit(): void {
    if (!this.isLoading && !this.product) throw new Error('Product property is required');
  }

  handleClick(): void {
    this.onClickProduct.emit(this.product);
  }

}

import { Component } from '@angular/core';
import { ProductsService } from '../../services/products.service';
import { Product } from '../../interfaces/product';
import { Router } from '@angular/router';

@Component({
  templateUrl: './product-catalog-page.component.html'
})
export class ProductCatalogPageComponent {

  public products: Product[] = [];
  public isBusy: boolean = false;

  constructor(
    private router: Router,
    private productService: ProductsService
  ) { }

  ngOnInit(): void {
    this.searchByName('');
  }

  searchByName(query: string): void {
    this.isBusy = true;
    this.productService
      .searchProductsByName(query)
      .subscribe(products => {
        this.products = products;
        this.isBusy = false;
      });
  }

  handleViewDetails(product: Product): void {
    this.router.navigateByUrl(`/products/${product.id}`);
  }

}

import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { switchMap } from 'rxjs';
import { Product } from '../../interfaces/product';
import { ProductsService } from '../../services/products.service';

@Component({
  templateUrl: './product-details-page.component.html'
})
export class ProductDetailsPageComponent {

  public isBusy: boolean = false;
  public product?: Product;

  constructor(
    private router: Router,
    private activatedRoute: ActivatedRoute,
    private productsService: ProductsService
  ) { }

  ngOnInit() {
    this.isBusy = true;
    this.activatedRoute.params
      .pipe(
        switchMap( ({ id }) => this.productsService.getProductById( Number(id) ) )
      )
      .subscribe( product => {
        this.isBusy = false;
        if ( !product ) {
          return this.router.navigateByUrl('');
        }
        return this.product = product;
      });
  }

  goBack() {
    this.router.navigateByUrl('../');
  }

}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Product } from '../../interfaces/product';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html'
})
export class ProductListComponent {

  /**
   * Número de tarjetas de carga a mostrar
   */
  @Input()
  public loadingCardsCount: number = 4;

  /**
   * Indica si se están cargando los productos
   */
  @Input()
  public isLoading: boolean = false;

  /**
   * Lista de productos a mostrar
   */
  @Input()
  public products: Product[] = [];

  /**
   * Evento que se emite cuando se hace click en un producto
   */
  @Output()
  public onViewDetails = new EventEmitter<Product>();

  get emptyProducts(): number[] {
    return Array(this.loadingCardsCount).fill(0);
  }

  handleClickProduct(product: Product): void {
    this.onViewDetails.emit(product);
  }

}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Subject, debounceTime } from 'rxjs';

@Component({
  selector: 'shared-search-bar',
  template: `
    <input
      type="text"
      class="form-control"
      [placeholder]="placeholder"
      [ariaLabel]="placeholder"
      (keyup)="handleKeyPress(searchInput.value)"
      #searchInput
    />
  `
})
export class SearchBarComponent implements OnInit {

  /**
   * Placeholder a mostrar en el campo de búsqueda
   */
  @Input()
  public placeholder: string = '';

  /**
   * Evento que se emite cuando se escribe en el campo de búsqueda
   */
  @Output()
  public onSearch = new EventEmitter<string>();

  private debouncer = new Subject<string>();

  ngOnInit(): void {
    this.debouncer
      .pipe(
        debounceTime(300)
      )
      .subscribe(query => this.onSearch.emit(query));
  }

  public handleKeyPress(query: string) {
    this.debouncer.next(query);
  }

}

